{
  "query": "query ($network: String!, $address: String!, $limit: Int!) { blockchain(network: $network) { transactions(options: {desc: \"block.timestamp.time\", limit: $limit}, tx: {any: [{sender: {is: $address}}, {receiver: {is: $address}}]}) { hash sender receiver value block { timestamp { time } } } } }",
